import React, { useEffect, FC } from "react"
import { TextStyle, View, ViewStyle } from "react-native"
import { StackScreenProps } from "@react-navigation/stack"
import { observer } from "mobx-react-lite"
import { Header, Screen, Text, GradientBackground } from "../../components"
import { color, spacing } from "../../theme"
import { useStores } from "../../models"
import { NavigatorParamList } from "../../navigators"

const BOLD: TextStyle = { fontWeight: "bold" }

const FULL: ViewStyle = {
  flex: 1,
}
const CONTAINER: ViewStyle = {
  backgroundColor: color.transparent,
}
const HEADER: TextStyle = {
  paddingBottom: spacing[5] - 1,
  paddingHorizontal: spacing[4],
  paddingTop: spacing[3],
}
const HEADER_TITLE: TextStyle = {
  fontSize: 12,
  fontWeight: "bold",
  letterSpacing: 1.5,
  lineHeight: 15,
  textAlign: "center",
}

const TITLE: TextStyle = {
  ...BOLD,
  fontSize: 28,
  lineHeight: 38,
  textAlign: "center",
  marginBottom: spacing[5],
}

export const BrotherListScreen: FC<StackScreenProps<NavigatorParamList, "brotherList">> = observer(
  ({ route, navigation }) => {
    const { brotherId } = route.params
    const goBack = () => navigation.goBack()

    const { brotherStore } = useStores()
    const { brothers } = brotherStore

    const [isLoading, setIsLoading] = React.useState(false);

    useEffect(() => {
      brotherStore.clear()

      async function fetchData() {
        setIsLoading(true);
        await brotherStore.getBrother(brotherId)
        setIsLoading(false);
      }

      fetchData()
    }, [])

    return (
      <View testID="DemoListScreen" style={FULL}>
        <GradientBackground colors={["#422443", "#281b34"]} />
        <Screen style={CONTAINER} preset="fixed" backgroundColor={color.transparent}>
          <Header
            headerTx="demoListScreen.title"
            leftIcon="back"
            onLeftPress={goBack}
            style={HEADER}
            titleStyle={HEADER_TITLE}
          />
          <Text style={TITLE} preset="header" tx="verifyScreen.verified" />

          <View> 
            {
              (isLoading)
                ?<Text>Carregando</Text>
                :<Text>Finalizado</Text>
            } 
          </View>
        </Screen>
      </View>
    )
  },
)
