import * as React from "react"
import { View, Text, Image, FlatList, ViewStyle, TextStyle, ImageStyle } from "react-native"
import { spacing } from "../../theme"

const LIST_CONTAINER: ViewStyle = {
  alignItems: "center",
  flexDirection: "row",
  padding: 10,
}
const IMAGE: ImageStyle = {
  borderRadius: 50,
  height: 150,
  width: 150,
}
const LIST_TEXT: TextStyle = {
  marginLeft: 10,
}
const FLAT_LIST: ViewStyle = {
  paddingHorizontal: spacing[4],
}

export function BrotherList(brothers: any) {
  console.log('carregou brothers', brothers)

  return (
    <FlatList
      contentContainerStyle={FLAT_LIST}
      data={[...brothers]}
      keyExtractor={(item) => String(item.cadastro)}
      renderItem={({ item }) => (
        <>
          <View style={LIST_CONTAINER}>
            <Image source={{ uri: item.foto }} style={IMAGE} />
          </View>
          <View>
            <Text style={LIST_TEXT}>
              Cadastro: {item.cadastro}
            </Text>
            <Text style={LIST_TEXT}>
              Nome: {item.nome}
            </Text>
            <Text style={LIST_TEXT}>
              Loja: {item.loja}
            </Text>
            <Text style={LIST_TEXT}>
              Situação: {item.situacao}
            </Text>
          </View>
        </>
      )}
    />
  )
}
